function randomStartRGB(){let r=Math.floor(3*Math.random())+1,t=Math.floor(3*Math.random())+1;for(;r==t;)t=Math.floor(3*Math.random())+1;let a={r:0,g:0,b:0},n={r:0,g:0,b:0};function o(r,t){return 1==t?r.r=255:2==t?r.g=255:r.b=255,r}return[a=o(a,r),n=o(n,t)]}function fadeRGB(r){return r.r>0&&0==r.b&&(r.r--,r.g++),r.g>0&&0==r.r&&(r.g--,r.b++),r.b>0&&0==r.g&&(r.r++,r.b--),r}function flipRGBval(r){return 255-r}let[p1,p2]=randomStartRGB();setInterval(function(){p1=fadeRGB(p1),p2=fadeRGB(p2),document.body.style.background="linear-gradient(45deg, rgb("+p1.r+", "+p1.g+", "+p1.b+") 0%, rgb("+flipRGBval(p2.r)+", "+flipRGBval(p2.g)+", "+flipRGBval(p2.b)+") 100%)"},30);